import osfrom flask import Flaskfrom flask_sqlalchemy import SQLAlchemyfrom config import Configuration, DevelopmentConfigapp = Flask(__name__, static_url_path='/static')app.config.from_object(os.environ['APP_SETTINGS'])app.config.from_object(Configuration)app.config['SQLALCHEMY_TRACK_MODIFICATIONS'] = Falsedb = SQLAlchemy(app)db.init_app(app)import modelsimport routesimport utilsfrom routes import *if __name__ == "__main__":    app.run()# if __name__ == '__main__':  # pragma: no cover#     app.run(port=80)